fun getRottenTomatoesSystem(): RottenTomatoesSystem {
    val rottenTomatoesSystem = RottenTomatoesSystem()
    addCategories(rottenTomatoesSystem)
    addMovies(rottenTomatoesSystem)
    addRelatedMovies(rottenTomatoesSystem)
    addUsers(rottenTomatoesSystem)
    addReview(rottenTomatoesSystem)
    return rottenTomatoesSystem
}

// Categories

private fun addCategories(rottenTomatoesSystem: RottenTomatoesSystem) {
    rottenTomatoesSystem.addCategory(DraftCategory("Action"))
    rottenTomatoesSystem.addCategory(DraftCategory("Adventure"))
    rottenTomatoesSystem.addCategory(DraftCategory("Animation"))
    rottenTomatoesSystem.addCategory(DraftCategory("Comedy"))
    rottenTomatoesSystem.addCategory(DraftCategory("Crime"))
    rottenTomatoesSystem.addCategory(DraftCategory("Documentary"))
    rottenTomatoesSystem.addCategory(DraftCategory("Drama"))
    rottenTomatoesSystem.addCategory(DraftCategory("Family"))
    rottenTomatoesSystem.addCategory(DraftCategory("Fantasy"))
    rottenTomatoesSystem.addCategory(DraftCategory("History"))
    rottenTomatoesSystem.addCategory(DraftCategory("Horror"))
    rottenTomatoesSystem.addCategory(DraftCategory("Music"))
    rottenTomatoesSystem.addCategory(DraftCategory("Mystery"))
    rottenTomatoesSystem.addCategory(DraftCategory("Romance"))
    rottenTomatoesSystem.addCategory(DraftCategory("Science Fiction"))
    rottenTomatoesSystem.addCategory(DraftCategory("TV Movie"))
    rottenTomatoesSystem.addCategory(DraftCategory("Thriller"))
    rottenTomatoesSystem.addCategory(DraftCategory("War"))
    rottenTomatoesSystem.addCategory(DraftCategory("Western"))
}

// Movies

private fun addMovies(rottenTomatoesSystem: RottenTomatoesSystem) {
    getMoviesData().map { rottenTomatoesSystem.addMovie(getDraftMovie(rottenTomatoesSystem, it)) }
}

