private fun getDraftMovie(rottenTomatoesSystem: RottenTomatoesSystem, movieData: MovieData): DraftMovie {
    val categories = movieData.categories.map { c -> rottenTomatoesSystem.categories.find { it.name == c }!! }.toMutableList()
    return DraftMovie(movieData.title, movieData.description, movieData.poster, categories)
}

private fun addRelatedMovies(rottenTomatoesSystem: RottenTomatoesSystem) {
    rottenTomatoesSystem.movies.forEach {
        it.relatedContent = getRandomIndexes(5).map { index -> rottenTomatoesSystem.movies[index] }.toMutableList()
    }
}

