// Reviews

private fun addReview(rottenTomatoesSystem: RottenTomatoesSystem) {
    rottenTomatoesSystem.users.forEach { user ->
        val moviesIds = getRandomIndexes(15).map { index -> rottenTomatoesSystem.movies[index].id }
        moviesIds.forEach { movieId ->
            val textIndex = getRandomIndexes(1, 15)[0]
            rottenTomatoesSystem.addReview(DraftReview(user.id, movieId, reviewsText[textIndex], getRandomIndexes(1, 6)[0]))
        }
    }
}
